
IF EXISTS (select * from tempdb..sysobjects where id = object_id('tempdb..#ACTIVIDADES'))
	DROP TABLE #ACTIVIDADES

CREATE TABLE #ACTIVIDADES
(Codigo int identity(1,1),
 Descripcion Varchar (80) COLLATE DATABASE_DEFAULT NOT NULL,
 CodInterfaz Varchar(30) COLLATE DATABASE_DEFAULT NOT NULL
)

	BEGIN TRANSACTION

	BEGIN TRY
		--INSERT INTO #ACTIVIDADES (CodInterfaz, Descripcion) SELECT '551023',LEFT('Servicios de alojamiento en hoteles, hosterías y residenciales similares, excepto por hora, que no incluyen servicio de restaurante al público',80)

		--SELECT * FROM #ACTIVIDADES WHERE Descripcion = 'Servicios de asesoramiento, dirección y gestión empresarial realizados por inte1'


		------------------
		DECLARE @CodInterfaz CodigoInterfazLargo
		DECLARE @Descripcion DescripcionMedia
		DECLARE @DescripcionOrig DescripcionMedia
		DECLARE @DescripcionFormat DescripcionMedia
		DECLARE @CodAuditoriaRef CodigoLargo
		DECLARE @Repetidos Int
		DECLARE @Index Int

		SET @Descripcion = ''

		/*FORMATEO DESCRIPCIONES REPETIDAS*/
		DECLARE ACTIVIDAD_FORMAT_CURSOR CURSOR READ_ONLY FOR 
			SELECT #ACTIVIDADES.Descripcion, #ACTIVIDADES.CodInterfaz, TEMP.Repetido
			FROM #ACTIVIDADES
			     INNER JOIN (SELECT Descripcion,
									COUNT(*) AS Repetido
							FROM #ACTIVIDADES
							GROUP BY Descripcion
							HAVING COUNT(*) > 1) TEMP ON TEMP.Descripcion = #ACTIVIDADES.Descripcion

		-- Apertura del cursor
		OPEN ACTIVIDAD_FORMAT_CURSOR
		
		-- Lectura de la primera fila del cursor
		FETCH NEXT FROM ACTIVIDAD_FORMAT_CURSOR INTO @DescripcionOrig, @CodInterfaz, @Repetidos

		WHILE (@@FETCH_STATUS = 0 )
		BEGIN
			
			IF @Descripcion <> @DescripcionOrig 
			BEGIN
				SET @Index = 1
				SET @Descripcion = @DescripcionOrig 
			END
			ELSE
			BEGIN
				SET @Index = @Index + 1
			END

			SET @DescripcionFormat =  LEFT(@Descripcion, 80 - LEN(@Repetidos))  
			SET @DescripcionFormat = @DescripcionFormat + CONVERT(VARCHAR, @Index)

			UPDATE #ACTIVIDADES SET #ACTIVIDADES.Descripcion = @DescripcionFormat WHERE CodInterfaz = @CodInterfaz

			-- Lectura de la siguiente fila del cursor
			FETCH NEXT FROM ACTIVIDAD_FORMAT_CURSOR INTO @DescripcionOrig, @CodInterfaz, @Repetidos
		END

		-- Cierre del cursor
		CLOSE ACTIVIDAD_FORMAT_CURSOR
		-- Liberar los recursos
		DEALLOCATE ACTIVIDAD_FORMAT_CURSOR


		--SELECT * FROM #ACTIVIDADES WHERE Descripcion like ('%Servicios de asesoramiento, dirección y gestión empresarial%')
		------------------
		

		/* Recorro las actividades, cargo su auditoriaref correspondiente e inserto */
		/*********************************************************/
		DECLARE ACTIVIDADES_CURSOR CURSOR READ_ONLY FOR 
			SELECT CodInterfaz,
				   Descripcion
			FROM #ACTIVIDADES
			ORDER BY CodInterfaz

		-- Apertura del cursor
		OPEN ACTIVIDADES_CURSOR
		
		-- Lectura de la primera fila del cursor
		FETCH NEXT FROM ACTIVIDADES_CURSOR INTO @CodInterfaz, @Descripcion

		WHILE (@@FETCH_STATUS = 0 )
		BEGIN
			IF NOT EXISTS (SELECT 1 FROM ACTIVIDADES WHERE CodInterfaz = @CodInterfaz)
			BEGIN
				SET @CodAuditoriaRef = @@IDENTITY
				
				PRINT 'Info: ' + @CodInterfaz + ' - ' + @Descripcion + ' - ' + convert(varchar, @CodAuditoriaRef)

			END
			
			-- Lectura de la siguiente fila del cursor
			FETCH NEXT FROM ACTIVIDADES_CURSOR INTO @CodInterfaz, @Descripcion
		END

		-- Cierre del cursor
		CLOSE ACTIVIDADES_CURSOR
		-- Liberar los recursos
		DEALLOCATE ACTIVIDADES_CURSOR

	END TRY

	BEGIN CATCH
		SELECT   
			ERROR_NUMBER() AS ErrorNumber  
			,ERROR_SEVERITY() AS ErrorSeverity  
			,ERROR_STATE() AS ErrorState  
			,ERROR_PROCEDURE() AS ErrorProcedure  
			,ERROR_LINE() AS ErrorLine  
			,ERROR_MESSAGE() AS ErrorMessage;  

		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION
	END CATCH

	IF @@TRANCOUNT > 0
		print 'COMMIT OKA: ' + CONVERT(VARCHAR, @@TRANCOUNT)
		COMMIT TRANSACTION